import React, { Component } from 'react';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';

/* Images */
import carouselImg1 from '../assets/stock1.jpeg';
import carouselImg2 from '../assets/img2.jpg';
import carouselImg3 from '../assets/stock3.jpeg';
import carouselImg4 from '../assets/stock4.jpeg';
import carouselImg5 from '../assets/stock8.jpeg';
import carouselImg6 from '../assets/stock11.jpeg';

class AccCarousel extends Component{
   constructor(props) {
    super(props);

    this.state = {
        showThumbs: false,
        carouselData: [
            { img:carouselImg1, content:"ACC PROVIDES SOLUTIONS"},
            { img:carouselImg2, content:"Proven Track Record Over 27 Years"},
            { img:carouselImg3, content:"Dedicated to Excellence and Customer Service"},
            { img:carouselImg4, content:"Experienced, Professional, and Qualified Staff"},
            { img:carouselImg5, content:"Customer First Strategy"},
            { img:carouselImg6, content:"Outstanding Reputation"}
        ]
    }
   }

   render(){
      return(
         <div className="carousel-body">
            <Carousel className="acc-carousel" showThumbs={this.state.showThumbs} showStatus={false} infiniteLoop autoPlay>
                {this.state.carouselData.map((item, i) => {
                        <div className="slide-container">
                            <img src={item.img} />
                            <p className="legend">{item.content}</p>
                        </div>
                })}
            </Carousel>
         </div>
      );
   }
}

export default AccCarousel;
