import React, { Component } from 'react';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';

/* Images */
import stock8 from '../assets/stock8.jpeg';
import stock9 from '../assets/stock9.jpeg';
import stock10 from '../assets/stock10.jpeg';
import stock11 from '../assets/stock11.jpeg';

class AccCarousel extends Component{
   constructor(props) {
    super(props);

    this.state = {
        showThumbs: false
    }
   }

   render(){
      return(
         <div className="carousel-body">
            <Carousel className="acc-carousel" showThumbs={this.state.showThumbs}>
                <div>
                    <img src={ stock8 } />
                    <p className="legend">Legend 1</p>
                </div>
                <div>
                    <img src={ stock9 } />
                    <p className="legend">Legend 2</p>
                </div>
                <div>
                    <img src={ stock10 } />
                    <p className="legend">Legend 3</p>
                </div>
            </Carousel>
         </div>
      );
   }
}

export default AccCarousel;
